# grouped by week, show number of requests

select * into outfile '/tmp/summaries.csv' fields terminated by ',' optionally enclosed by '"' lines terminated by '\n' from summariessed -i '' -e's/ \d\d:\d\d:\d\d//g' summaries.csv
sed -i '' -e's/-//g' summaries.csv
sed -i '' -e's/"//g' summaries.csv


A = load 'summaries.csv' using PigStorage(',') as (id:int, token_id:int, from:int, to:int, queries:int, match_count:int, foreign:int, us_isp:int, us_resolved_business:int, us_unresolved:int, created_at:int, updated_at:int, period:chararray, hot_list_matches: int);
B = GROUP A BY from;
C= FOREACH B GENERATE group, SUM(A.match_count), SUM(A.us_resolved_business);
DUMP C;

